package miniproject2.paymentmanagementsystem.config;
import miniproject2.paymentmanagementsystem.model.User;
import miniproject2.paymentmanagementsystem.model.UserRole;
import miniproject2.paymentmanagementsystem.repository.UserRepository;
import org.slf4j.Logger;
import org.slf4j.LoggerFactory;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.boot.CommandLineRunner;
import org.springframework.security.crypto.password.PasswordEncoder;
import org.springframework.stereotype.Component;

@Component
public class DataInitializer implements CommandLineRunner {

    private static final Logger logger = LoggerFactory.getLogger(DataInitializer.class);

    private final UserRepository userRepository;
    private final PasswordEncoder passwordEncoder;

    @Autowired
    public DataInitializer(UserRepository userRepository, PasswordEncoder passwordEncoder) {
        this.userRepository = userRepository;
        this.passwordEncoder = passwordEncoder;
    }

    @Override
    public void run(String... args) throws Exception {
        initializeDefaultUsers();
    }

    private void initializeDefaultUsers() {
        // Create default admin user if not exists
        if (!userRepository.existsByEmail("admin@paymentms.com")) {
            User admin = new User();
            admin.setName("System Administrator");
            admin.setEmail("admin@paymentms.com");
            admin.setPasswordHash(passwordEncoder.encode("admin123"));
            admin.setRole(UserRole.ADMIN);
            userRepository.save(admin);
            logger.info("Created default admin user: admin@paymentms.com");
        }

        // Create default finance manager if not exists
        if (!userRepository.existsByEmail("finance@paymentms.com")) {
            User financeManager = new User();
            financeManager.setName("Finance Manager");
            financeManager.setEmail("finance@paymentms.com");
            financeManager.setPasswordHash(passwordEncoder.encode("finance123"));
            financeManager.setRole(UserRole.FINANCE_MANAGER);
            userRepository.save(financeManager);
            logger.info("Created default finance manager: finance@paymentms.com");
        }

        // Create default viewer if not exists
        if (!userRepository.existsByEmail("viewer@paymentms.com")) {
            User viewer = new User();
            viewer.setName("Payment Viewer");
            viewer.setEmail("viewer@paymentms.com");
            viewer.setPasswordHash(passwordEncoder.encode("viewer123"));
            viewer.setRole(UserRole.VIEWER);
            userRepository.save(viewer);
            logger.info("Created default viewer user: viewer@paymentms.com");
        }
    }
}

package miniproject2.paymentmanagementsystem.config;

import miniproject2.paymentmanagementsystem.entity.User;
import miniproject2.paymentmanagementsystem.enums.Role;
import miniproject2.paymentmanagementsystem.repository.UserRepository;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.boot.CommandLineRunner;
import org.springframework.security.crypto.password.PasswordEncoder;
import org.springframework.stereotype.Component;

@Component
public class DataInitializer implements CommandLineRunner {

    @Autowired
    private UserRepository userRepository;

    @Autowired
    private PasswordEncoder passwordEncoder;

    @Override
    public void run(String... args) throws Exception {
        // Create default admin user if not exists
        if (!userRepository.existsByEmail("admin@example.com")) {
            User admin = new User();
            admin.setName("System Admin");
            admin.setEmail("admin@example.com");
            admin.setPassword(passwordEncoder.encode("admin123"));
            admin.setRole(Role.ADMIN);
            userRepository.save(admin);
            System.out.println("Default admin user created: admin@example.com / admin123");
        }

        // Create default finance manager if not exists
        if (!userRepository.existsByEmail("finance@example.com")) {
            User financeManager = new User();
            financeManager.setName("Finance Manager");
            financeManager.setEmail("finance@example.com");
            financeManager.setPassword(passwordEncoder.encode("finance123"));
            financeManager.setRole(Role.FINANCE_MANAGER);
            userRepository.save(financeManager);
            System.out.println("Default finance manager created: finance@example.com / finance123");
        }

        // Create default viewer if not exists
        if (!userRepository.existsByEmail("viewer@example.com")) {
            User viewer = new User();
            viewer.setName("Payment Viewer");
            viewer.setEmail("viewer@example.com");
            viewer.setPassword(passwordEncoder.encode("viewer123"));
            viewer.setRole(Role.VIEWER);
            userRepository.save(viewer);
            System.out.println("Default viewer user created: viewer@example.com / viewer123");
        }
    }
}

package miniproject2.paymentmanagementsystem.config;

import com.fasterxml.jackson.databind.ObjectMapper;
import com.fasterxml.jackson.datatype.jsr310.JavaTimeModule;
import org.springframework.context.annotation.Bean;
import org.springframework.context.annotation.Configuration;
import org.springframework.context.annotation.Primary;

@Configuration
public class JacksonConfig {

    @Bean
    @Primary
    public ObjectMapper objectMapper() {
        ObjectMapper mapper = new ObjectMapper();
        mapper.registerModule(new JavaTimeModule());
        return mapper;
    }
}

package miniproject2.paymentmanagementsystem.config;

import jakarta.servlet.FilterChain;
import jakarta.servlet.ServletException;
import jakarta.servlet.http.HttpServletRequest;
import jakarta.servlet.http.HttpServletResponse;
import miniproject2.paymentmanagementsystem.service.UserDetailsServiceImpl;
import miniproject2.paymentmanagementsystem.util.JwtUtil;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.security.authentication.UsernamePasswordAuthenticationToken;
import org.springframework.security.core.context.SecurityContextHolder;
import org.springframework.security.core.userdetails.UserDetails;
import org.springframework.security.web.authentication.WebAuthenticationDetailsSource;
import org.springframework.stereotype.Component;
import org.springframework.web.filter.OncePerRequestFilter;

import java.io.IOException;

@Component
public class JwtAuthenticationFilter extends OncePerRequestFilter {

    @Autowired
    private JwtUtil jwtUtil;

    @Autowired
    private UserDetailsServiceImpl userDetailsService;

    @Override
    protected void doFilterInternal(HttpServletRequest request, HttpServletResponse response, 
                                  FilterChain filterChain) throws ServletException, IOException {

        final String authHeader = request.getHeader("Authorization");
        final String jwt;
        final String userEmail;

        if (authHeader == null || !authHeader.startsWith("Bearer ")) {
            filterChain.doFilter(request, response);
            return;
        }

        jwt = authHeader.substring(7);
        userEmail = jwtUtil.extractUsername(jwt);

        if (userEmail != null && SecurityContextHolder.getContext().getAuthentication() == null) {
            UserDetails userDetails = this.userDetailsService.loadUserByUsername(userEmail);

            if (jwtUtil.validateToken(jwt, userDetails)) {
                UsernamePasswordAuthenticationToken authToken = 
                    new UsernamePasswordAuthenticationToken(
                        userDetails,
                        null,
                        userDetails.getAuthorities()
                    );
                authToken.setDetails(new WebAuthenticationDetailsSource().buildDetails(request));
                SecurityContextHolder.getContext().setAuthentication(authToken);
            }
        }
        filterChain.doFilter(request, response);
    }
}

