            if (choice >= 1 && choice <= allTeams.size()) {
                Team selectedTeam = allTeams.get(choice - 1);
                viewDetailedTeamMembers(selectedTeam);
            } else if (choice == allTeams.size() + 1) {
                viewAllUsersByRole();
            } else if (choice == allTeams.size() + 2) {
