package org.paymentmanagementsystem.repository;

import org.paymentmanagementsystem.config.DatabaseConfig;
import org.paymentmanagementsystem.model.Team;

import java.sql.*;
import java.time.LocalDateTime;
import java.util.ArrayList;
import java.util.List;

public class TeamRepository {
    private final DatabaseConfig dbConfig;

    public TeamRepository() throws Exception {
        this.dbConfig = DatabaseConfig.getInstance();
    }

    public Team createTeam(Team team) throws SQLException {
        String sql = """
            INSERT INTO teams (team_name, created_by_user_id)
            VALUES (?, ?)
            RETURNING team_id, created_date
            """;

        Connection conn = null;
        try {
            conn = dbConfig.getConnection();
            try (PreparedStatement stmt = conn.prepareStatement(sql)) {
                stmt.setString(1, team.getTeamName());
                stmt.setInt(2, team.getCreatedByUserId());

                try (ResultSet rs = stmt.executeQuery()) {
                    if (rs.next()) {
                        team.setTeamId(rs.getInt("team_id"));
                        team.setCreatedDate(rs.getTimestamp("created_date").toLocalDateTime());
                        return team;
                    }
                }
            }
        } finally {
            dbConfig.returnConnection(conn);
        }
        return null;
    }

    public Team findById(int teamId) throws SQLException {
        String sql = "SELECT * FROM teams WHERE team_id = ?";

        Connection conn = null;
        try {
            conn = dbConfig.getConnection();
            try (PreparedStatement stmt = conn.prepareStatement(sql)) {
                stmt.setInt(1, teamId);

                try (ResultSet rs = stmt.executeQuery()) {
                    if (rs.next()) {
                        return mapResultSetToTeam(rs);
                    }
                }
            }
        } finally {
            dbConfig.returnConnection(conn);
        }
        return null;
    }

    public Team findByName(String teamName) throws SQLException {
        String sql = "SELECT * FROM teams WHERE team_name = ?";

        Connection conn = null;
        try {
            conn = dbConfig.getConnection();
            try (PreparedStatement stmt = conn.prepareStatement(sql)) {
                stmt.setString(1, teamName);

                try (ResultSet rs = stmt.executeQuery()) {
                    if (rs.next()) {
                        return mapResultSetToTeam(rs);
                    }
                }
            }
        } finally {
            dbConfig.returnConnection(conn);
        }
        return null;
    }

    public List<Team> findByCreatedBy(int userId) throws SQLException {
        String sql = "SELECT * FROM teams WHERE created_by_user_id = ? ORDER BY created_date DESC";

        Connection conn = null;
        List<Team> teams = new ArrayList<>();
        try {
            conn = dbConfig.getConnection();
            try (PreparedStatement stmt = conn.prepareStatement(sql)) {
                stmt.setInt(1, userId);

                try (ResultSet rs = stmt.executeQuery()) {
                    while (rs.next()) {
                        teams.add(mapResultSetToTeam(rs));
                    }
                }
            }
        } finally {
            dbConfig.returnConnection(conn);
        }
        return teams;
    }

    public List<Team> findAll() throws SQLException {
        String sql = "SELECT * FROM teams ORDER BY team_name";

        Connection conn = null;
        List<Team> teams = new ArrayList<>();
        try {
            conn = dbConfig.getConnection();
            try (PreparedStatement stmt = conn.prepareStatement(sql)) {
                try (ResultSet rs = stmt.executeQuery()) {
                    while (rs.next()) {
                        teams.add(mapResultSetToTeam(rs));
                    }
                }
            }
        } finally {
            dbConfig.returnConnection(conn);
        }
        return teams;
    }

    public boolean updateTeam(Team team) throws SQLException {
        String sql = "UPDATE teams SET team_name = ? WHERE team_id = ?";

        Connection conn = null;
        try {
            conn = dbConfig.getConnection();
            try (PreparedStatement stmt = conn.prepareStatement(sql)) {
                stmt.setString(1, team.getTeamName());
                stmt.setInt(2, team.getTeamId());

                return stmt.executeUpdate() > 0;
            }
        } finally {
            dbConfig.returnConnection(conn);
        }
    }

    public boolean deleteTeam(int teamId) throws SQLException {
        String sql = "DELETE FROM teams WHERE team_id = ?";

        Connection conn = null;
        try {
            conn = dbConfig.getConnection();
            try (PreparedStatement stmt = conn.prepareStatement(sql)) {
                stmt.setInt(1, teamId);
                return stmt.executeUpdate() > 0;
            }
        } finally {
            dbConfig.returnConnection(conn);
        }
    }

    private Team mapResultSetToTeam(ResultSet rs) throws SQLException {
        Team team = new Team();
        team.setTeamId(rs.getInt("team_id"));
        team.setTeamName(rs.getString("team_name"));
        team.setCreatedByUserId(rs.getInt("created_by_user_id"));

        Timestamp timestamp = rs.getTimestamp("created_date");
        if (timestamp != null) {
            team.setCreatedDate(timestamp.toLocalDateTime());
        }

        return team;
    }
}

package org.paymentmanagementsystem.repository;

import org.paymentmanagementsystem.config.DatabaseConfig;
import org.paymentmanagementsystem.model.Role;
import org.paymentmanagementsystem.model.Team;
import org.paymentmanagementsystem.model.User;

import java.io.IOException;
import java.sql.*;
import java.util.ArrayList;
import java.util.List;
import java.util.Optional;

public class UserRepository {
    private final DatabaseConfig dbConfig;

    public UserRepository() throws SQLException, IOException {
        this.dbConfig = DatabaseConfig.getInstance();
    }

    public Optional<User> findByEmail(String email) throws SQLException {
        String sql = """
            SELECT u.*, r.role_name, t.team_name 
            FROM users u 
            LEFT JOIN roles r ON u.role_id = r.role_id 
            LEFT JOIN teams t ON u.team_id = t.team_id 
            WHERE u.email = ?
            """;

        Connection conn = null;
        try {
            conn = dbConfig.getConnection();
            try (PreparedStatement stmt = conn.prepareStatement(sql)) {
                stmt.setString(1, email);

                try (ResultSet rs = stmt.executeQuery()) {
                    if (rs.next()) {
                        return Optional.of(mapResultSetToUser(rs));
                    }
                }
            }
        } finally {
            dbConfig.returnConnection(conn);
        }
        return Optional.empty();
    }

    public User save(User user) throws SQLException {
        String sql = "INSERT INTO users (name, email, password_hash, role_id) VALUES (?, ?, ?, ?) RETURNING user_id";

        Connection conn = null;
        try {
            conn = dbConfig.getConnection();
            PreparedStatement stmt = conn.prepareStatement(sql);
            stmt.setString(1, user.getName());
            stmt.setString(2, user.getEmail());
            stmt.setString(3, user.getPasswordHash());
            stmt.setInt(4, user.getRoleId());

            ResultSet rs = stmt.executeQuery();
            if (rs.next()) {
                user.setUserId(rs.getInt("user_id"));
            }
            return user;
        } finally {
            dbConfig.returnConnection(conn);
        }
    }

    public User createUser(User user) throws SQLException {
        String sql = """
            INSERT INTO users (name, email, password_hash, role_id, team_id)
            VALUES (?, ?, ?, ?, ?)
            RETURNING user_id
            """;

        Connection conn = null;
        try {
            conn = dbConfig.getConnection();
            try (PreparedStatement stmt = conn.prepareStatement(sql)) {
                stmt.setString(1, user.getName());
                stmt.setString(2, user.getEmail());
                stmt.setString(3, user.getPasswordHash());
                stmt.setInt(4, user.getRoleId());

                if (user.getTeamId() != null) {
                    stmt.setInt(5, user.getTeamId());
                } else {
                    stmt.setNull(5, Types.INTEGER);
                }

                try (ResultSet rs = stmt.executeQuery()) {
                    if (rs.next()) {
                        user.setUserId(rs.getInt("user_id"));
                        return user;
                    }
                }
            }
        } finally {
            dbConfig.returnConnection(conn);
        }
        return null;
    }

    public List<User> findAll() throws SQLException {
        String sql = """
            SELECT u.user_id, u.name, u.email, u.password_hash, u.role_id,
                   r.role_name
            FROM users u
            JOIN roles r ON u.role_id = r.role_id
            """;

        Connection conn = null;
        List<User> users = new ArrayList<>();

        try {
            conn = dbConfig.getConnection();
            Statement stmt = conn.createStatement();
            ResultSet rs = stmt.executeQuery(sql);

            while (rs.next()) {
                users.add(mapResultSetToUser(rs));
            }
            return users;
        } finally {
            dbConfig.returnConnection(conn);
        }
    }

    public List<User> findByTeamId(int teamId) throws SQLException {
        String sql = """
            SELECT u.*, r.role_name, t.team_name 
            FROM users u 
            LEFT JOIN roles r ON u.role_id = r.role_id 
            LEFT JOIN teams t ON u.team_id = t.team_id 
            WHERE u.team_id = ?
            ORDER BY u.name
            """;

        Connection conn = null;
        List<User> users = new ArrayList<>();
        try {
            conn = dbConfig.getConnection();
            try (PreparedStatement stmt = conn.prepareStatement(sql)) {
                stmt.setInt(1, teamId);

                try (ResultSet rs = stmt.executeQuery()) {
                    while (rs.next()) {
                        users.add(mapResultSetToUser(rs));
                    }
                }
            }
        } finally {
            dbConfig.returnConnection(conn);
        }
        return users;
    }

    public void deleteById(int userId) throws SQLException {
        String sql = "DELETE FROM users WHERE user_id = ?";

        Connection conn = null;
        try {
            conn = dbConfig.getConnection();
            PreparedStatement stmt = conn.prepareStatement(sql);
            stmt.setInt(1, userId);
            stmt.executeUpdate();
        } finally {
            dbConfig.returnConnection(conn);
        }
    }

    public boolean assignUserToTeam(int userId, Integer teamId) throws SQLException {
        String sql = "UPDATE users SET team_id = ? WHERE user_id = ?";

        Connection conn = null;
        try {
            conn = dbConfig.getConnection();
            try (PreparedStatement stmt = conn.prepareStatement(sql)) {
                if (teamId != null) {
                    stmt.setInt(1, teamId);
                } else {
                    stmt.setNull(1, Types.INTEGER);
                }
                stmt.setInt(2, userId);

                return stmt.executeUpdate() > 0;
            }
        } finally {
            dbConfig.returnConnection(conn);
        }
    }

    private User mapResultSetToUser(ResultSet rs) throws SQLException {
        User user = new User();
        user.setUserId(rs.getInt("user_id"));
        user.setName(rs.getString("name"));
        user.setEmail(rs.getString("email"));
        user.setPasswordHash(rs.getString("password_hash"));
        user.setRoleId(rs.getInt("role_id"));

        // Handle team_id which can be null
        int teamId = rs.getInt("team_id");
        if (!rs.wasNull()) {
            user.setTeamId(teamId);
        }

        // Set role information
        Role role = new Role();
        role.setRoleId(user.getRoleId());
        role.setRoleName(rs.getString("role_name"));
        user.setRole(role);

        // Set team information if available
        String teamName = rs.getString("team_name");
        if (teamName != null) {
            Team team = new Team();
            team.setTeamId(user.getTeamId());
            team.setTeamName(teamName);
            user.setTeam(team);
        }

        return user;
    }
}

package org.paymentmanagementsystem.repository;

import org.paymentmanagementsystem.config.DatabaseConfig;
