                throw new RuntimeException("Error creating payment", e);
            }
        }, executorService);
    }

    public List<Payment> getPaymentsByUser(User currentUser) throws PaymentException {
        try {
            String role = currentUser.getRole().getRoleName().toLowerCase();

            switch (role) {
                case "admin":
                case "finance_manager":
                    return paymentRepository.findAll();
                case "viewer":
                    return paymentRepository.findByUserId(currentUser.getUserId());
                default:
                    throw new AuthorizationException("Invalid role");
            }
        } catch (SQLException | AuthorizationException e) {
            throw new PaymentException("Error retrieving payments", e);
        }
    }

    public CompletableFuture<Void> approvePayment(int paymentId, User currentUser) {
        return CompletableFuture.runAsync(() -> {
            try {
                if (!hasApprovalPermission(currentUser)) {
                    throw new AuthorizationException("User does not have approval permission");
                }

                Optional<Payment> paymentOpt = paymentRepository.findById(paymentId);
                if (paymentOpt.isEmpty()) {
                    throw new PaymentException("Payment not found");
                }

                Payment payment = paymentOpt.get();

                // Get approved status
                org.paymentmanagementsystem.model.Status approvedStatus = statusRepository.findByName("APPROVED");
                if (approvedStatus == null) {
                    throw new PaymentException("Approved status not found");
                }

                // Update status
                paymentRepository.updateStatus(paymentId, approvedStatus.getStatusId());

                // Log audit trail
                auditService.logStatusChange(payment, currentUser, "APPROVED");

            } catch (Exception e) {
                throw new RuntimeException("Error approving payment", e);
            }
        }, executorService);
    }

    public CompletableFuture<Void> rejectPayment(int paymentId, User currentUser) {
        return CompletableFuture.runAsync(() -> {
            try {
                if (!hasApprovalPermission(currentUser)) {
                    throw new AuthorizationException("User does not have approval permission");
                }

                Optional<Payment> paymentOpt = paymentRepository.findById(paymentId);
                if (paymentOpt.isEmpty()) {
                    throw new PaymentException("Payment not found");
                }

                Payment payment = paymentOpt.get();

                // Get rejected status
                org.paymentmanagementsystem.model.Status rejectedStatus = statusRepository.findByName("REJECTED");
                if (rejectedStatus == null) {
                    throw new PaymentException("Rejected status not found");
                }

                // Update status
                paymentRepository.updateStatus(paymentId, rejectedStatus.getStatusId());

                // Log audit trail
                auditService.logStatusChange(payment, currentUser, "REJECTED");

            } catch (Exception e) {
                throw new RuntimeException("Error rejecting payment", e);
            }
        }, executorService);
    }

    public List<Payment> getPaymentsByStatus(String statusName, User currentUser) throws PaymentException {
        try {
            List<Payment> allPayments = getPaymentsByUser(currentUser);

            return allPayments.stream()
                    .filter(payment -> payment.getStatus().getStatusName().equalsIgnoreCase(statusName))
                    .collect(Collectors.toList());

        } catch (Exception e) {
            throw new PaymentException("Error filtering payments by status", e);
        }
    }

    public CompletableFuture<Void> deletePayment(int paymentId, User currentUser) {
        return CompletableFuture.runAsync(() -> {
            try {
                if (!hasDeletePermission(currentUser)) {
                    throw new AuthorizationException("User does not have delete permission");
                }

                Optional<Payment> paymentOpt = paymentRepository.findById(paymentId);
                if (paymentOpt.isEmpty()) {
                    throw new PaymentException("Payment not found");
                }

                Payment payment = paymentOpt.get();

                // Log audit trail before deletion
                auditService.logPaymentDeletion(payment, currentUser);

                // Delete payment
                paymentRepository.deleteById(paymentId);

            } catch (Exception e) {
                throw new RuntimeException("Error deleting payment", e);
            }
        }, executorService);
    }

    private boolean hasApprovalPermission(User user) {
        String role = user.getRole().getRoleName().toLowerCase();
        return role.equals("admin") || role.equals("finance_manager");
    }

    private boolean hasDeletePermission(User user) {
        String role = user.getRole().getRoleName().toLowerCase();
        return role.equals("admin");
    }

    public void shutdown() {
        executorService.shutdown();
    }
}

package org.paymentmanagementsystem.service;

import org.paymentmanagementsystem.model.Payment;
import org.paymentmanagementsystem.model.User;
import org.paymentmanagementsystem.repository.PaymentRepository;
import org.paymentmanagementsystem.repository.UserRepository;

import java.math.BigDecimal;
import java.sql.SQLException;
import java.time.LocalDate;
import java.time.YearMonth;
import java.time.format.DateTimeFormatter;
import java.util.HashMap;
import java.util.List;
import java.util.Map;
import java.util.stream.Collectors;

public class ReportService {
    private final PaymentRepository paymentRepository;
    private final UserRepository userRepository;

    public ReportService() throws Exception {
        this.paymentRepository = new PaymentRepository();
        this.userRepository = new UserRepository();
    }

    /**
     * Generate monthly report for a specific month and year
     */
    public MonthlyReport generateMonthlyReport(int year, int month) throws SQLException {
        YearMonth yearMonth = YearMonth.of(year, month);
        LocalDate startDate = yearMonth.atDay(1);
        LocalDate endDate = yearMonth.atEndOfMonth();

        List<Payment> payments = getPaymentsByDateRange(startDate, endDate);
        return createMonthlyReport(payments, yearMonth);
    }

    /**
     * Generate quarterly report for a specific quarter and year
     */
    public QuarterlyReport generateQuarterlyReport(int year, int quarter) throws SQLException {
        if (quarter < 1 || quarter > 4) {
            throw new IllegalArgumentException("Quarter must be between 1 and 4");
        }

        // Calculate quarter date range
        int startMonth = (quarter - 1) * 3 + 1;
        YearMonth startYearMonth = YearMonth.of(year, startMonth);
        YearMonth endYearMonth = startYearMonth.plusMonths(2);

        LocalDate startDate = startYearMonth.atDay(1);
        LocalDate endDate = endYearMonth.atEndOfMonth();

        List<Payment> payments = getPaymentsByDateRange(startDate, endDate);
        return createQuarterlyReport(payments, year, quarter, startDate, endDate);
    }

    /**
     * Generate current month report
     */
    public MonthlyReport generateCurrentMonthReport() throws SQLException {
        YearMonth currentMonth = YearMonth.now();
        return generateMonthlyReport(currentMonth.getYear(), currentMonth.getMonthValue());
    }

    /**
     * Generate current quarter report
     */
    public QuarterlyReport generateCurrentQuarterReport() throws SQLException {
        LocalDate today = LocalDate.now();
        int quarter = (today.getMonthValue() - 1) / 3 + 1;
        return generateQuarterlyReport(today.getYear(), quarter);
    }

    private List<Payment> getPaymentsByDateRange(LocalDate startDate, LocalDate endDate) throws SQLException {
        List<Payment> allPayments = paymentRepository.findAll();
        return allPayments.stream()
                .filter(payment -> {
                    LocalDate paymentDate = payment.getPaymentDate().toLocalDate();
                    return !paymentDate.isBefore(startDate) && !paymentDate.isAfter(endDate);
                })
                .collect(Collectors.toList());
    }

    private MonthlyReport createMonthlyReport(List<Payment> payments, YearMonth yearMonth) {
        MonthlyReport report = new MonthlyReport();
        report.setYear(yearMonth.getYear());
        report.setMonth(yearMonth.getMonthValue());
        report.setMonthName(yearMonth.getMonth().name());

        // Calculate totals
        report.setTotalPayments(payments.size());
        report.setTotalAmount(calculateTotalAmount(payments));

        // Calculate by status
        Map<String, Integer> paymentsByStatus = new HashMap<>();
        Map<String, BigDecimal> amountsByStatus = new HashMap<>();

        for (Payment payment : payments) {
            String status = payment.getStatus().getStatusName();
            paymentsByStatus.merge(status, 1, Integer::sum);
            amountsByStatus.merge(status, payment.getAmount(), BigDecimal::add);
        }

        report.setPaymentsByStatus(paymentsByStatus);
        report.setAmountsByStatus(amountsByStatus);

        // Calculate by type
        Map<String, Integer> paymentsByType = new HashMap<>();
        Map<String, BigDecimal> amountsByType = new HashMap<>();

        for (Payment payment : payments) {
            String type = payment.getType();
            paymentsByType.merge(type, 1, Integer::sum);
            amountsByType.merge(type, payment.getAmount(), BigDecimal::add);
        }

        report.setPaymentsByType(paymentsByType);
        report.setAmountsByType(amountsByType);

        // Calculate by category
        Map<String, Integer> paymentsByCategory = new HashMap<>();
        Map<String, BigDecimal> amountsByCategory = new HashMap<>();

        for (Payment payment : payments) {
            String category = payment.getCategory().getCategoryName();
            paymentsByCategory.merge(category, 1, Integer::sum);
            amountsByCategory.merge(category, payment.getAmount(), BigDecimal::add);
        }

        report.setPaymentsByCategory(paymentsByCategory);
        report.setAmountsByCategory(amountsByCategory);

        return report;
    }

    private QuarterlyReport createQuarterlyReport(List<Payment> payments, int year, int quarter, 
                                                LocalDate startDate, LocalDate endDate) {
        QuarterlyReport report = new QuarterlyReport();
        report.setYear(year);
        report.setQuarter(quarter);
        report.setStartDate(startDate);
        report.setEndDate(endDate);

        // Calculate totals
        report.setTotalPayments(payments.size());
        report.setTotalAmount(calculateTotalAmount(payments));

        // Group payments by month within the quarter
        Map<YearMonth, List<Payment>> paymentsByMonth = payments.stream()
                .collect(Collectors.groupingBy(p -> YearMonth.from(p.getPaymentDate())));

        Map<String, MonthlyReport> monthlyReports = new HashMap<>();
        for (Map.Entry<YearMonth, List<Payment>> entry : paymentsByMonth.entrySet()) {
            YearMonth month = entry.getKey();
            String monthKey = month.format(DateTimeFormatter.ofPattern("yyyy-MM"));
            monthlyReports.put(monthKey, createMonthlyReport(entry.getValue(), month));
        }

        report.setMonthlyBreakdown(monthlyReports);

        // Calculate by status for entire quarter
        Map<String, Integer> paymentsByStatus = new HashMap<>();
        Map<String, BigDecimal> amountsByStatus = new HashMap<>();

        for (Payment payment : payments) {
            String status = payment.getStatus().getStatusName();
            paymentsByStatus.merge(status, 1, Integer::sum);
            amountsByStatus.merge(status, payment.getAmount(), BigDecimal::add);
        }

        report.setPaymentsByStatus(paymentsByStatus);
        report.setAmountsByStatus(amountsByStatus);

        // Calculate by type for entire quarter
        Map<String, Integer> paymentsByType = new HashMap<>();
        Map<String, BigDecimal> amountsByType = new HashMap<>();

        for (Payment payment : payments) {
            String type = payment.getType();
            paymentsByType.merge(type, 1, Integer::sum);
            amountsByType.merge(type, payment.getAmount(), BigDecimal::add);
        }

        report.setPaymentsByType(paymentsByType);
        report.setAmountsByType(amountsByType);

        return report;
    }

    private BigDecimal calculateTotalAmount(List<Payment> payments) {
        return payments.stream()
                .map(Payment::getAmount)
                .reduce(BigDecimal.ZERO, BigDecimal::add);
    }

    // Inner classes for report data structures
    public static class MonthlyReport {
        private int year;
        private int month;
        private String monthName;
        private int totalPayments;
        private BigDecimal totalAmount;
        private Map<String, Integer> paymentsByStatus;
        private Map<String, BigDecimal> amountsByStatus;
        private Map<String, Integer> paymentsByType;
        private Map<String, BigDecimal> amountsByType;
        private Map<String, Integer> paymentsByCategory;
        private Map<String, BigDecimal> amountsByCategory;

        // Getters and setters
        public int getYear() { return year; }
        public void setYear(int year) { this.year = year; }

        public int getMonth() { return month; }
        public void setMonth(int month) { this.month = month; }

        public String getMonthName() { return monthName; }
        public void setMonthName(String monthName) { this.monthName = monthName; }

        public int getTotalPayments() { return totalPayments; }
        public void setTotalPayments(int totalPayments) { this.totalPayments = totalPayments; }

        public BigDecimal getTotalAmount() { return totalAmount; }
        public void setTotalAmount(BigDecimal totalAmount) { this.totalAmount = totalAmount; }

        public Map<String, Integer> getPaymentsByStatus() { return paymentsByStatus; }
        public void setPaymentsByStatus(Map<String, Integer> paymentsByStatus) { this.paymentsByStatus = paymentsByStatus; }

        public Map<String, BigDecimal> getAmountsByStatus() { return amountsByStatus; }
        public void setAmountsByStatus(Map<String, BigDecimal> amountsByStatus) { this.amountsByStatus = amountsByStatus; }

        public Map<String, Integer> getPaymentsByType() { return paymentsByType; }
        public void setPaymentsByType(Map<String, Integer> paymentsByType) { this.paymentsByType = paymentsByType; }

        public Map<String, BigDecimal> getAmountsByType() { return amountsByType; }
        public void setAmountsByType(Map<String, BigDecimal> amountsByType) { this.amountsByType = amountsByType; }

        public Map<String, Integer> getPaymentsByCategory() { return paymentsByCategory; }
        public void setPaymentsByCategory(Map<String, Integer> paymentsByCategory) { this.paymentsByCategory = paymentsByCategory; }

        public Map<String, BigDecimal> getAmountsByCategory() { return amountsByCategory; }
        public void setAmountsByCategory(Map<String, BigDecimal> amountsByCategory) { this.amountsByCategory = amountsByCategory; }
    }

    public static class QuarterlyReport {
        private int year;
        private int quarter;
        private LocalDate startDate;
        private LocalDate endDate;
        private int totalPayments;
        private BigDecimal totalAmount;
        private Map<String, Integer> paymentsByStatus;
        private Map<String, BigDecimal> amountsByStatus;
        private Map<String, Integer> paymentsByType;
        private Map<String, BigDecimal> amountsByType;
        private Map<String, MonthlyReport> monthlyBreakdown;

        // Getters and setters
        public int getYear() { return year; }
        public void setYear(int year) { this.year = year; }

        public int getQuarter() { return quarter; }
        public void setQuarter(int quarter) { this.quarter = quarter; }

        public LocalDate getStartDate() { return startDate; }
        public void setStartDate(LocalDate startDate) { this.startDate = startDate; }

        public LocalDate getEndDate() { return endDate; }
        public void setEndDate(LocalDate endDate) { this.endDate = endDate; }

        public int getTotalPayments() { return totalPayments; }
        public void setTotalPayments(int totalPayments) { this.totalPayments = totalPayments; }

        public BigDecimal getTotalAmount() { return totalAmount; }
        public void setTotalAmount(BigDecimal totalAmount) { this.totalAmount = totalAmount; }

        public Map<String, Integer> getPaymentsByStatus() { return paymentsByStatus; }
        public void setPaymentsByStatus(Map<String, Integer> paymentsByStatus) { this.paymentsByStatus = paymentsByStatus; }

        public Map<String, BigDecimal> getAmountsByStatus() { return amountsByStatus; }
        public void setAmountsByStatus(Map<String, BigDecimal> amountsByStatus) { this.amountsByStatus = amountsByStatus; }

        public Map<String, Integer> getPaymentsByType() { return paymentsByType; }
        public void setPaymentsByType(Map<String, Integer> paymentsByType) { this.paymentsByType = paymentsByType; }

        public Map<String, BigDecimal> getAmountsByType() { return amountsByType; }
        public void setAmountsByType(Map<String, BigDecimal> amountsByType) { this.amountsByType = amountsByType; }

        public Map<String, MonthlyReport> getMonthlyBreakdown() { return monthlyBreakdown; }
        public void setMonthlyBreakdown(Map<String, MonthlyReport> monthlyBreakdown) { this.monthlyBreakdown = monthlyBreakdown; }
    }
}

package org.paymentmanagementsystem.service;

import org.paymentmanagementsystem.model.Payment;
import org.paymentmanagementsystem.model.User;
import org.paymentmanagementsystem.repository.CategoryRepository;
import org.paymentmanagementsystem.repository.PaymentRepository;
import org.paymentmanagementsystem.repository.StatusRepository;
import org.paymentmanagementsystem.repository.UserRepository;

import java.math.BigDecimal;
import java.sql.SQLException;
import java.time.LocalDate;
import java.time.LocalDateTime;
import java.time.YearMonth;
import java.util.List;

public class SalaryService {
    private final UserRepository userRepository;
    private final PaymentRepository paymentRepository;
    private final CategoryRepository categoryRepository;
    private final StatusRepository statusRepository;
    private final AuditService auditService;

    public SalaryService() throws Exception {
        this.userRepository = new UserRepository();
        this.paymentRepository = new PaymentRepository();
        this.categoryRepository = new CategoryRepository();
        this.statusRepository = new StatusRepository();
        this.auditService = new AuditService();
    }

    public void generateMonthlySalaryPayments() throws SQLException {
        YearMonth currentMonth = YearMonth.now();
        generateSalaryPaymentsForMonth(currentMonth);
    }

    public void generateSalaryPaymentsForMonth(YearMonth yearMonth) throws SQLException {
        List<User> usersWithSalary = userRepository.findUsersWithSalary();

        System.out.println("\n=== GENERATING MONTHLY SALARY PAYMENTS ===");
        System.out.println("Month: " + yearMonth);
        System.out.println("Eligible users: " + usersWithSalary.size());

        int successCount = 0;
        int skipCount = 0;

        Integer salaryCategoryId = getSalaryCategoryId();
        Integer approvedStatusId = getApprovedStatusId();

        for (User user : usersWithSalary) {
            try {
                // Check if user has salary and effective date
                if (user.getMonthlySalary() == null || user.getMonthlySalary().compareTo(BigDecimal.ZERO) <= 0) {
                    System.out.println("  Skipping " + user.getName() + " - No salary set");
                    skipCount++;
                    continue;
                }

                if (user.getSalaryEffectiveDate() == null) {
                    System.out.println("  Skipping " + user.getName() + " - No effective date set");
                    skipCount++;
                    continue;
                }

                // Check if salary is effective for this month
                YearMonth effectiveMonth = YearMonth.from(user.getSalaryEffectiveDate());
                if (yearMonth.isBefore(effectiveMonth)) {
                    System.out.println("  Skipping " + user.getName() + " - Salary not effective yet");
                    skipCount++;
                    continue;
                }

                // Check if salary payment already exists for this month
                if (salaryPaymentExistsForMonth(user.getUserId(), yearMonth)) {
                    System.out.println("  Skipping " + user.getName() + " - Salary already paid for " + yearMonth);
                    skipCount++;
                    continue;
                }

                // Create salary payment
                Payment salaryPayment = new Payment();
                salaryPayment.setAmount(user.getMonthlySalary());
                salaryPayment.setType("SALARY");
                salaryPayment.setDescription("Monthly salary for " + yearMonth + " - " + user.getName());
                salaryPayment.setCategoryId(salaryCategoryId);
                salaryPayment.setStatusId(approvedStatusId); // Auto-approve salary payments
                salaryPayment.setCreatedByUserId(user.getUserId()); // Salary is "created by" the user receiving it
                salaryPayment.setTeamId(user.getTeamId());
                salaryPayment.setPaymentDate(LocalDateTime.now());

                // Save the payment
                Payment createdPayment = paymentRepository.save(salaryPayment);

                if (createdPayment != null) {
                    // Create audit trail
                    auditService.logPaymentAction(createdPayment.getPaymentId(), user.getUserId(),
                        "SALARY_GENERATED", null, "Automatic monthly salary payment generated");

                    System.out.println(" Generated salary payment for " + user.getName() +
                        " - Amount: $" + user.getMonthlySalary() + " (Payment ID: " + createdPayment.getPaymentId() + ")");
                    successCount++;
                } else {
                    System.out.println(" Failed to create salary payment for " + user.getName());
                }

            } catch (Exception e) {
                System.out.println(" Error processing salary for " + user.getName() + ": " + e.getMessage());
            }
        }

        System.out.println("\n=== SALARY GENERATION COMPLETE ===");
        System.out.println(" Successfully generated: " + successCount + " payments");
        System.out.println("  Skipped: " + skipCount + " users");
        System.out.println("Total processed: " + (successCount + skipCount) + " users");
    }

    /**
     * Check if a salary payment already exists for a user in a specific month
     */
    private boolean salaryPaymentExistsForMonth(int userId, YearMonth yearMonth) throws SQLException {
        // Check if there's already a salary payment for this user this month
        LocalDate startOfMonth = yearMonth.atDay(1);
        LocalDate endOfMonth = yearMonth.atEndOfMonth();

        return paymentRepository.existsSalaryPaymentForUserInPeriod(userId, startOfMonth, endOfMonth);
    }

    /**
     * Get or create the "Salary" category
     */
    private Integer getSalaryCategoryId() throws SQLException {
        var salaryCategory = categoryRepository.findByName("Salary");
        if (salaryCategory != null) {
            return salaryCategory.getCategoryId();
        }

        // Create salary category if it doesn't exist
        var newCategory = categoryRepository.createCategory("Salary");
        return newCategory != null ? newCategory.getCategoryId() : 1; // Fallback to first category
    }

    /**
     * Get the APPROVED status ID
     */
    private Integer getApprovedStatusId() throws SQLException {
        var approvedStatus = statusRepository.findByName("APPROVED");
        return approvedStatus != null ? approvedStatus.getStatusId() : 2; // Fallback to status ID 2
    }

    /**
     * Update a user's monthly salary
     */
    public boolean updateUserSalary(int userId, BigDecimal monthlySalary, LocalDate effectiveDate) throws SQLException {
        return userRepository.updateUserSalary(userId, monthlySalary, effectiveDate);
    }

    /**
     * Get all users with salary information
     */
    public List<User> getUsersWithSalary() throws SQLException {
        return userRepository.findUsersWithSalary();
    }

    /**
     * Generate salary payments for a specific user for the current month
     */
    public boolean generateSalaryForUser(int userId) throws SQLException {
        var userOptional = userRepository.findByEmail(getUserEmailById(userId));
        if (userOptional.isEmpty()) {
            return false;
        }

        User user = userOptional.get();
        YearMonth currentMonth = YearMonth.now();

        // Check eligibility
        if (user.getMonthlySalary() == null || user.getMonthlySalary().compareTo(BigDecimal.ZERO) <= 0) {
            return false;
        }

        if (salaryPaymentExistsForMonth(userId, currentMonth)) {
            return false; // Already paid this month
        }

        // Generate payment for this user only
        List<User> singleUserList = List.of(user);
        generateSalaryPaymentsForMonth(currentMonth);

        return true;
    }

    private String getUserEmailById(int userId) throws SQLException {
        List<User> allUsers = userRepository.findAll();
        return allUsers.stream()
                .filter(u -> u.getUserId() == userId)
                .map(User::getEmail)
                .findFirst()
                .orElse("unknown@unknown.com");
    }
}

package org.paymentmanagementsystem.service;

import org.paymentmanagementsystem.model.Payment;
import org.paymentmanagementsystem.model.User;
import org.paymentmanagementsystem.repository.CategoryRepository;
import org.paymentmanagementsystem.repository.PaymentRepository;
import org.paymentmanagementsystem.repository.StatusRepository;
import org.paymentmanagementsystem.repository.UserRepository;

import java.math.BigDecimal;
import java.sql.SQLException;
import java.time.LocalDate;
import java.time.LocalDateTime;
import java.time.YearMonth;
import java.util.List;

public class SalaryService {
    private final UserRepository userRepository;
    private final PaymentRepository paymentRepository;
    private final CategoryRepository categoryRepository;
    private final StatusRepository statusRepository;
    private final AuditService auditService;

    public SalaryService() throws Exception {
        this.userRepository = new UserRepository();
        this.paymentRepository = new PaymentRepository();
        this.categoryRepository = new CategoryRepository();
        this.statusRepository = new StatusRepository();
        this.auditService = new AuditService();
    }

    public void generateMonthlySalaryPayments() throws SQLException {
        YearMonth currentMonth = YearMonth.now();
        generateSalaryPaymentsForMonth(currentMonth);
    }

    public void generateSalaryPaymentsForMonth(YearMonth yearMonth) throws SQLException {
        List<User> usersWithSalary = userRepository.findUsersWithSalary();

        System.out.println("\n=== GENERATING MONTHLY SALARY PAYMENTS ===");
        System.out.println("Month: " + yearMonth);
        System.out.println("Eligible users: " + usersWithSalary.size());

        int successCount = 0;
        int skipCount = 0;

        Integer salaryCategoryId = getSalaryCategoryId();
        Integer approvedStatusId = getApprovedStatusId();

        for (User user : usersWithSalary) {
            try {
                // Check if user has salary and effective date
                if (user.getMonthlySalary() == null || user.getMonthlySalary().compareTo(BigDecimal.ZERO) <= 0) {
                    System.out.println("  Skipping " + user.getName() + " - No salary set");
                    skipCount++;
                    continue;
                }

                if (user.getSalaryEffectiveDate() == null) {
                    System.out.println("  Skipping " + user.getName() + " - No effective date set");
                    skipCount++;
                    continue;
                }

                // Check if salary is effective for this month
                YearMonth effectiveMonth = YearMonth.from(user.getSalaryEffectiveDate());
                if (yearMonth.isBefore(effectiveMonth)) {
                    System.out.println("  Skipping " + user.getName() + " - Salary not effective yet");
                    skipCount++;
                    continue;
                }

                // Check if salary payment already exists for this month
                if (salaryPaymentExistsForMonth(user.getUserId(), yearMonth)) {
                    System.out.println("  Skipping " + user.getName() + " - Salary already paid for " + yearMonth);
                    skipCount++;
                    continue;
                }

                // Create salary payment
                Payment salaryPayment = new Payment();
                salaryPayment.setAmount(user.getMonthlySalary());
                salaryPayment.setType("SALARY");
                salaryPayment.setDescription("Monthly salary for " + yearMonth + " - " + user.getName());
                salaryPayment.setCategoryId(salaryCategoryId);
                salaryPayment.setStatusId(approvedStatusId); // Auto-approve salary payments
                salaryPayment.setCreatedByUserId(user.getUserId()); // Salary is "created by" the user receiving it
                salaryPayment.setTeamId(user.getTeamId());
                salaryPayment.setPaymentDate(LocalDateTime.now());

                // Save the payment
                Payment createdPayment = paymentRepository.save(salaryPayment);

                if (createdPayment != null) {
                    // Create audit trail using the existing logPaymentCreation method
                    User auditUser = new User();
                    auditUser.setUserId(user.getUserId());
                    auditService.logPaymentCreation(createdPayment, auditUser);

                    System.out.println(" Generated salary payment for " + user.getName() + 
                        " - Amount: $" + user.getMonthlySalary() + " (Payment ID: " + createdPayment.getPaymentId() + ")");
                    successCount++;
                } else {
                    System.out.println(" Failed to create salary payment for " + user.getName());
                }

            } catch (Exception e) {
                System.out.println(" Error processing salary for " + user.getName() + ": " + e.getMessage());
            }
        }

        System.out.println("\n=== SALARY GENERATION COMPLETE ===");
        System.out.println(" Successfully generated: " + successCount + " payments");
        System.out.println("  Skipped: " + skipCount + " users");
        System.out.println("Total processed: " + (successCount + skipCount) + " users");
    }

    /**
     * Check if a salary payment already exists for a user in a specific month
     */
    private boolean salaryPaymentExistsForMonth(int userId, YearMonth yearMonth) throws SQLException {
        // Check if there's already a salary payment for this user this month
        LocalDate startOfMonth = yearMonth.atDay(1);
        LocalDate endOfMonth = yearMonth.atEndOfMonth();

        return paymentRepository.existsSalaryPaymentForUserInPeriod(userId, startOfMonth, endOfMonth);
    }

    /**
     * Get or create the "Salary" category
     */
    private Integer getSalaryCategoryId() throws SQLException {
        var salaryCategory = categoryRepository.findByName("Salary");
        if (salaryCategory != null) {
            return salaryCategory.getCategoryId();
        }

        // Create salary category if it doesn't exist
        var newCategory = categoryRepository.createCategory("Salary");
        return newCategory != null ? newCategory.getCategoryId() : 1; // Fallback to first category
    }

    /**
     * Get the APPROVED status ID
     */
    private Integer getApprovedStatusId() throws SQLException {
        var approvedStatus = statusRepository.findByName("APPROVED");
        return approvedStatus != null ? approvedStatus.getStatusId() : 2; // Fallback to status ID 2
    }

    /**
     * Update a user's monthly salary
     */
    public boolean updateUserSalary(int userId, BigDecimal monthlySalary, LocalDate effectiveDate) throws SQLException {
        return userRepository.updateUserSalary(userId, monthlySalary, effectiveDate);
    }

    /**
     * Get all users with salary information
     */
    public List<User> getUsersWithSalary() throws SQLException {
        return userRepository.findUsersWithSalary();
    }

    /**
     * Generate salary payments for a specific user for the current month
     */
    public boolean generateSalaryForUser(int userId) throws SQLException {
        var userOptional = userRepository.findByEmail(getUserEmailById(userId));
        if (userOptional.isEmpty()) {
            return false;
        }

        User user = userOptional.get();
        YearMonth currentMonth = YearMonth.now();

        // Check eligibility
        if (user.getMonthlySalary() == null || user.getMonthlySalary().compareTo(BigDecimal.ZERO) <= 0) {
            return false;
        }

        if (salaryPaymentExistsForMonth(userId, currentMonth)) {
            return false; // Already paid this month
        }

        // Generate payment for this user only
        List<User> singleUserList = List.of(user);
        generateSalaryPaymentsForMonth(currentMonth);

        return true;
    }

    private String getUserEmailById(int userId) throws SQLException {
        List<User> allUsers = userRepository.findAll();
        return allUsers.stream()
                .filter(u -> u.getUserId() == userId)
                .map(User::getEmail)
                .findFirst()
                .orElse("unknown@unknown.com");
    }
}

package org.paymentmanagementsystem.util;

import java.sql.*;
import java.util.Properties;

public class DatabaseInitializer {

    public static void initializeDatabase(Properties dbProperties) throws SQLException, ClassNotFoundException {
        String fullUrl = dbProperties.getProperty("db.url");
        String username = dbProperties.getProperty("db.username");
        String password = dbProperties.getProperty("db.password");
        String driver = dbProperties.getProperty("db.driver");

        // Load PostgreSQL driver
        Class.forName(driver);

        // Extract database name
        String[] urlParts = fullUrl.split("/");
        String databaseName = urlParts[urlParts.length - 1];
        String postgresUrl = fullUrl.substring(0, fullUrl.lastIndexOf('/')) + "/postgres";

        System.out.println("Checking database existence...");

        // Connect to postgres database to create target database if needed
        try (Connection conn = DriverManager.getConnection(postgresUrl, username, password);
             Statement stmt = conn.createStatement()) {

            // Check if database exists
            String checkDbQuery = "SELECT 1 FROM pg_database WHERE datname = '" + databaseName + "'";
            boolean dbExists = stmt.executeQuery(checkDbQuery).next();

            if (!dbExists) {
                System.out.println("Creating database: " + databaseName);
                stmt.executeUpdate("CREATE DATABASE " + databaseName);
                System.out.println("Database created successfully!");
            } else {
                System.out.println("Database already exists: " + databaseName);
            }
        }

        // Now connect to the target database and create schema
        try (Connection conn = DriverManager.getConnection(fullUrl, username, password)) {
            boolean isFirstTimeSetup = !schemaExists(conn);

            System.out.println("First time setup: " + isFirstTimeSetup);

            if (isFirstTimeSetup) {
                System.out.println("Creating database schema...");
                createSchema(conn);
                System.out.println("Database schema created successfully!");

                // Create default admin user on first setup
                createDefaultAdminUser(conn);
            } else {
                System.out.println("Database schema already exists.");
            }
        }
    }

    private static boolean schemaExists(Connection conn) throws SQLException {
        String checkQuery = """
            SELECT COUNT(*) FROM information_schema.tables 
            WHERE table_schema = 'public' 
            AND table_name IN ('roles', 'teams', 'users', 'categories', 'status', 'payments', 'audit_trail')
            """;

        try (Statement stmt = conn.createStatement()) {
            ResultSet rs = stmt.executeQuery(checkQuery);
            if (rs.next()) {
                return rs.getInt(1) == 7; // All 7 tables must exist (including teams)
            }
        }
        return false;
    }

    private static void createSchema(Connection conn) throws SQLException {
        String[] schemaStatements = {
                // Create roles table
                """
            CREATE TABLE roles (
                role_id SERIAL PRIMARY KEY,
                role_name VARCHAR(50) NOT NULL UNIQUE
            )
            """,

                // Create teams table (without foreign key initially)
                """
            CREATE TABLE teams (
                team_id SERIAL PRIMARY KEY,
                team_name VARCHAR(100) NOT NULL UNIQUE,
                created_by_user_id INTEGER NOT NULL,
                created_date TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP
            )
            """,

                // Create users table
                """
            CREATE TABLE users (
                user_id SERIAL PRIMARY KEY,
                name VARCHAR(100) NOT NULL,
                email VARCHAR(100) NOT NULL UNIQUE,
                password_hash VARCHAR(255) NOT NULL,
                role_id INTEGER NOT NULL,
                team_id INTEGER,
                FOREIGN KEY (role_id) REFERENCES roles(role_id),
                FOREIGN KEY (team_id) REFERENCES teams(team_id)
            )
            """,

                // Create categories table
                """
            CREATE TABLE categories (
                category_id SERIAL PRIMARY KEY,
                category_name VARCHAR(100) NOT NULL UNIQUE
            )
            """,

                // Create status table
                """
            CREATE TABLE status (
                status_id SERIAL PRIMARY KEY,
                status_name VARCHAR(50) NOT NULL UNIQUE
            )
            """,

                // Create payments table
                """
            CREATE TABLE payments (
                payment_id SERIAL PRIMARY KEY,
                amount DECIMAL(15,2) NOT NULL,
                type VARCHAR(50) NOT NULL,
                payment_date TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP,
                description TEXT,
                category_id INTEGER NOT NULL,
                status_id INTEGER NOT NULL,
                created_by_user_id INTEGER NOT NULL,
                team_id INTEGER,
                FOREIGN KEY (category_id) REFERENCES categories(category_id),
                FOREIGN KEY (status_id) REFERENCES status(status_id),
                FOREIGN KEY (created_by_user_id) REFERENCES users(user_id),
                FOREIGN KEY (team_id) REFERENCES teams(team_id)
            )
            """,

                // Create audit_trail table
                """
            CREATE TABLE audit_trail (
                audit_id SERIAL PRIMARY KEY,
                payment_id INTEGER NOT NULL,
                user_id INTEGER NOT NULL,
                action VARCHAR(50) NOT NULL,
                change_timestamp TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP,
                old_value TEXT,
                new_value TEXT,
                FOREIGN KEY (payment_id) REFERENCES payments(payment_id),
                FOREIGN KEY (user_id) REFERENCES users(user_id)
            )
            """,

                // Add foreign key constraint to teams table (after users table is created)
                """
            ALTER TABLE teams 
            ADD CONSTRAINT fk_teams_created_by 
            FOREIGN KEY (created_by_user_id) REFERENCES users(user_id)
            """,

                // Insert default roles
                """
            INSERT INTO roles (role_name) VALUES 
            ('admin'),
            ('finance_manager'),
            ('viewer')
            """,

                // Insert default categories
                """
            INSERT INTO categories (category_name) VALUES 
            ('Office Supplies'),
            ('Travel'),
            ('Equipment'),
            ('Software'),
            ('Utilities')
            """,

                // Insert default status values
                """
            INSERT INTO status (status_name) VALUES 
            ('PENDING'),
            ('APPROVED'),
            ('REJECTED')
            """,

                // Create indexes for better performance
                "CREATE INDEX idx_payments_created_by ON payments(created_by_user_id)",
                "CREATE INDEX idx_payments_status ON payments(status_id)",
                "CREATE INDEX idx_payments_date ON payments(payment_date)",
                "CREATE INDEX idx_payments_team ON payments(team_id)",
                "CREATE INDEX idx_users_team ON users(team_id)",
                "CREATE INDEX idx_audit_trail_payment ON audit_trail(payment_id)"
        };

        try (Statement stmt = conn.createStatement()) {
            conn.setAutoCommit(false); // Start transaction

            for (String statement : schemaStatements) {
                String trimmed = statement.trim();
                if (!trimmed.isEmpty()) {
                    stmt.executeUpdate(trimmed);
                }
            }

            conn.commit();
            conn.setAutoCommit(true);
        } catch (SQLException e) {
            conn.rollback();
            conn.setAutoCommit(true);
            throw e;
        }
    }

    private static void createDefaultAdminUser(Connection conn) throws SQLException {
        System.out.println("\n" + "=".repeat(50));
        System.out.println("FIRST TIME SETUP - CREATING DEFAULT ADMIN USER");
        System.out.println("=".repeat(50));

        try {
            // Hash the default admin password using BCrypt (same as PasswordUtil)
            String hashedPassword = PasswordUtil.hashPassword("admin@123");

            // Insert default admin user
            String insertAdminQuery = """
                INSERT INTO users (name, email, password_hash, role_id) 
                VALUES (?, ?, ?, (SELECT role_id FROM roles WHERE role_name = 'admin'))
                """;

            try (PreparedStatement stmt = conn.prepareStatement(insertAdminQuery)) {
                stmt.setString(1, "admin");
                stmt.setString(2, "admin@admin.tech");
                stmt.setString(3, hashedPassword);

                int rowsInserted = stmt.executeUpdate();

                if (rowsInserted > 0) {
                    System.out.println(" Default admin user created successfully!");
                    System.out.println();
                    System.out.println("DEFAULT ADMIN CREDENTIALS:");
                    System.out.println("Name:     admin");
                    System.out.println("Email:    admin@admin.tech");
                    System.out.println("Password: admin@123");
                    System.out.println("Role:     admin");
                    System.out.println();
                    System.out.println("  IMPORTANT: Please change the default password after first login!");
                    System.out.println("=".repeat(50));
                    System.out.println();
                } else {
                    throw new SQLException("No rows inserted for default admin user");
                }
            }

        } catch (Exception e) {
            System.err.println("Failed to create default admin user: " + e.getMessage());
            throw new SQLException("Error creating default admin user", e);
        }
    }
}

